        string memory symbolArg,
        string memory baseTokenURIArg,
        address fusionImplementationArg
    ) ERC721PresetMinterPauserAutoId(nameArg, symbolArg, baseTokenURIArg) {
        fusionImplementation = fusionImplementationArg;
        factory = msg.sender;
        registry = PolyNftFactory(factory).registry();
    }

    // TODO: add access control, owner, registry or everyone
    function mint(
        address to,
        string memory tokenURI,
        bytes memory attribute,
        string memory description,
        uint256[] memory fusionSourceTokenIds
    ) external {
        uint256 tokenId = mint(to);

        _tokenURIMap[tokenId] = tokenURI;
        attributeMap[tokenId] = attribute;
        descriptionMap[tokenId] = description;
        fusionSourceTokenIdsMap[tokenId] = fusionSourceTokenIds;
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        return bytes(_tokenURIMap[tokenId]).length > 0 ? _tokenURIMap[tokenId] : _baseURI();
    }

    function getFusionSourceTokenIds(uint256 tokenId) public view returns(uint256[] memory) {
        return fusionSourceTokenIdsMap[tokenId];
    }

    function getTokenIdsByOwner(address ownerArg) public view returns(uint256[] memory) {
        uint256 balance = balanceOf(ownerArg);
        uint256[] memory tokens = new uint256[](balance);
        uint256 index = 0;
        for (uint256 i = 0; i < balance; i++) {
            uint256 tokenId = tokenOfOwnerByIndex(ownerArg, i);
            if (ownerOf(tokenId) == ownerArg) {
                tokens[index] = tokenId;
                index++;
            }
        }
        return tokens;
    }

    function getAllTokenInfoByOwner(address ownerArg) public view returns(TokenInfo[] memory) {
        uint256[] memory tokenIds = getTokenIdsByOwner(ownerArg);
        TokenInfo[] memory allTokensInfo = new TokenInfo[](tokenIds.length);
        for (uint256 i = 0; i < tokenIds.length; i++) {
            allTokensInfo[i] = getTokenInfo(tokenIds[i]);
        }
        return allTokensInfo;
    }

    function getTokenInfo(uint256 tokenId) public view returns (TokenInfo memory) {
        return TokenInfo(
            tokenId,
            tokenURI(tokenId),
            attributeMap[tokenId],
            descriptionMap[tokenId],
            fusionSourceTokenIdsMap[tokenId]
        );
    }

    function getAllTokensIndo() public view returns(TokenInfo[] memory) {
        uint256 totalSupply = totalSupply();
        TokenInfo[] memory tokens = new TokenInfo[](totalSupply);
        for (uint256 i = 0; i < totalSupply; i++) {
            tokens[i] = getTokenInfo(i);
        }
        return tokens;
    }
}
","keccak256":"0xd87343aa15298334ca253fca858023a585167aa017394336e8e0581e86b0cdd5"},"contracts/PolyNftFactory.sol":{"content":"pragma solidity 0.8.1;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { PolyNftErc721 } from "./PolyNftErc721.sol";

contract PolyNftFactory is Ownable {
    address public registry;
    mapping(address => address) public erc721Owner;

    // TODO: need to add whitelist logic to prevent non-allowed project
    string[] public projects;
    mapping(string => address) public projectMap;

    constructor(address registryArg) {
        registry = registryArg;
    }

    function create(
        string calldata projectName,
        string calldata name,
        string calldata symbol,
        string calldata tokenURI,
        address fusionImplementation
    ) external returns (address) {
        // PNF_SPN: same project name
        require(projectMap[projectName] == address(0), "PNF_SPN");

        PolyNftErc721 erc721 = new PolyNftErc721(name, symbol, tokenURI, fusionImplementation);
        address erc721Addr = address(erc721);
        erc721Owner[erc721Addr] = msg.sender;

        projects.push(projectName);
        projectMap[projectName] = erc721Addr;

        return erc721Addr;
    }

    function getProjects() external view returns(string[] memory) {
        return projects;
    }
}
